import React, { Component } from 'react';
import axios from 'axios';

import Header from './Header';
import Body from './Body';
import Footer from './Footer';

import './App.css';

class App extends Component {

	constructor(props) {
        super(props);
		this.state = {type: null, data: [{'name':null, 'age':null}]};
		this.useApiFunc = this.useApiFunc.bind(this);
    }

	 useApiFunc = async (e) => {
        let callType = e.target.attributes.getNamedItem('type').value;

        if (callType === "Block") {
            // console.log(callType)
            let data1 = [{'name':"virat","age":"31"}];
            this.setState({type: callType, data: data1});
        } else if (callType === "Transaction") {
            console.log(callType)
            let data2 = [{'name':"rohit","age":"32"}];
            this.setState({type: callType, data: data2});
        }
        // console.log(callType)
        // this.setState({type: callType});
		// console.log(this.state)

		// getUser = (e) => {
			// e.preventDefault();
			// const user = e.target.elements.username.value;
			// if (user) {
			  await axios.get(`https://blockchain.info/rawblock/0000000000000bae09a7a393a8acded75aa67e46cb81f7acaa5ad94f9eacd103`)
			  .then((res) => {
				console.log(res,"finally")
				// const repos = res.data.public_repos;
				// this.setState({ repos });
			  })
			// } else return;
		//   }
	}

	// componentWillMount() {
    //     console.log(this.state,"virat will")
    // }
    // componentDidMount() {
    //     console.log(this.state,"virat did")
    // }

	render() {
		return ( 
			<div> 
				<Header />
				<Body onclickevent={this.useApiFunc} type={this.state.type} data={this.state.data} />
				<Footer />
			</div>
		);
	}
}
export default App;
