import React from 'react';

import Header from './Header';
import Body from './Body';
import Footer from './Footer';

import './App.css';

class App extends React.Component {

	constructor(props) {
        super(props);
        this.state = {type: null, data: [{'name':null, 'age':null}]};
    }

	useApiFunc = (e) => {
        let callType = e.target.attributes.getNamedItem('types').value;

        if (callType == "Block") {
            console.log(callType)
            let data1 = [{'name':"virat","age":"31"}];
            this.setState({type: "Block", data: data1});
        } else if (callType == "Transaction") {
            console.log(callType)
            let data2 = [{'name':"rohit","age":"32"}];
            this.setState({type: "Transaction", data: data2});
        }
        // console.log(callType)
        // this.setState({type: callType});
        console.log(this.state.data[0].name)
	}

	render() {
		return ( 
			<div> 
				<Header />
				<Body />
				<Footer />
			</div>
		);
	}
}
export default App;
