        //     this.setState({type: callType, data: data2});
        // }
        // console.log(callType)
        // this.setState({type: callType});
		// console.log(this.state)

		// getUser = (e) => {
			// e.preventDefault();
			// const user = e.target.elements.username.value;
			// if (user) {

			var config = {
				headers: {'Access-Control-Allow-Origin': '*'}
			};
			await axios({url: `https://blockchain.info/latestblock?format=json&cors=true`
			  , method: `get`
			  , dataType: `json`}, config)
			  .then((res) => {
				console.log(res.data,"finally")
				this.setState({type: callType, data: [res.data]});
			  }).catch(function (error) {
				console.log(error,"yes");
			  })
			  .finally(function () {
				// always executed
			  });
			// } else return;
		//   }
	}

	// componentWillMount() {
    //     console.log(this.state,"virat will")
    // }
    // componentDidMount() {
    //     console.log(this.state,"virat did")
    // }

	render() {
		return ( 
			<div> 
				<Header />
				<Body onclickevent={this.useApiFunc} type={this.state.type} data={this.state.data} />
				<Footer />
			</div>
		);
	}
}
export default App;
