import React from 'react';



class Next extends React.Component {
    constructor(props) {
        super(props);
        this.state = {type: this.props.types};
    }

    useApiFunc = (e) => {
        let callType = e.target.attributes.getNamedItem('types').value;

        // if (callType == "Block") {
        //     this.setState({type: "Block"});
        // } else if (callType == "Transaction") {
        //     this.setState({type: "Transaction"});
        // }
        console.log(callType)
        this.setState({type: callType});
        console.log(this.state.type)
    }

    render() {
        console.log("mahesh", this.state.type);
        return (
            <div>
                <i onClick={this.useApiFunc} types={this.props.types} data-toggle="modal" data-target="#myModal" style={{fontSize:'24px',cursor:'pointer'}} className="fa">&#xf101;</i>
            </div>
        );
    }
}
export default Next;
