import React from 'react';

import Block from './Block';
import Transaction from './Transaction';
import Where from './Where';
import Testimonial from './Testimonial';

import Mymodal from './Buttons/Mymodal';

class Body extends React.Component {

    constructor(props) {
        super(props);
        this.state = {type: null, data: []};
    }

    useApiFunc = (e) => {
        let callType = e.target.attributes.getNamedItem('types').value;

        if (callType == "Block") {
            let data1 = [{'name':"virat","age":"31"},{'name':"rohit","age":"32"}];
            this.setState({type: "Block", data: data1});
        } else if (callType == "Transaction") {
            let data2 = [];
            this.setState({type: "Transaction", data: data2});
        }
        // console.log(callType)
        // this.setState({type: callType});
        console.log(this.state)
    }

    render() {
        return (
            <div>
                <Block onclickevent={this.useApiFunc} />
                <Transaction onclickevent={this.useApiFunc} />
                <Where />
                <Testimonial />        
                <Mymodal types={this.state.type} />
            </div>
        );
    }
}

export default Body;
