import React, { Component } from 'react';

import Header from './Header';
import Body from './Body';
import Footer from './Footer';

import './App.css';

class App extends Component {

	constructor(props) {
        super(props);
		this.state = {type: null, data: [{'name':null, 'age':null}]};
		this.useApiFunc = this.useApiFunc.bind(this);
    }

	useApiFunc = (e) => {
        let callType = e.target.attributes.getNamedItem('type').value;

        if (callType === "Block") {
            // console.log(callType)
            let data1 = [{'name':"virat","age":"31"}];
            this.setState({type: callType, data: data1});
        } else if (callType === "Transaction") {
            console.log(callType)
            let data2 = [{'name':"rohit","age":"32"}];
            this.setState({type: callType, data: data2});
        }
        // console.log(callType)
        // this.setState({type: callType});
        // console.log(this.state)
	}

	// componentWillMount() {
    //     console.log(this.state,"virat will")
    // }
    // componentDidMount() {
    //     console.log(this.state,"virat did")
    // }

	render() {
		return ( 
			<div> 
				<Header />
				<Body onclickevent={this.useApiFunc} type={this.state.type} data={this.state.data} />
				<Footer />
			</div>
		);
	}
}
export default App;
